import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import AuthForm from './components/Auth/AuthForm';
import Dashboard from './pages/Dashboard';
import { useAuth } from './hooks/useAuth';

function App() {
  const { user } = useAuth();

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={!user ? <AuthForm /> : user.emailVerified ? <Navigate to="/dashboard" /> : <EmailVerifyNotice />}
        />
        <Route
          path="/dashboard"
          element={user && user.emailVerified ? <Dashboard /> : <Navigate to="/" />}
        />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

const EmailVerifyNotice = () => (
  <div style={{ padding: '40px', textAlign: 'center', color: '#fff' }}>
    <h2>Please Verify Your Email</h2>
    <p>Check your inbox and verify your email before accessing the dashboard.</p>
  </div>
);

export default App;
